function ifScript() {  
            var check = 'no';  
            workflow.scratchpad.counter++;  // increment the counter  
          
            // check the counter and see if we need to go again or stop  
            if (workflow.scratchpad.counter < workflow.scratchpad.count) {  
                        check = 'yes';  
            }  
          
            return check;  
}

