function ifScript(){
	gs.info("Workflow : "+ JSON.stringify(workflow));
		
	if(workflow.scratchpad.userApprovalSend >= workflow.scratchpad.count){
		return 'yes';
	}
	return 'no';
}

// Set the variable 'answer' to '', 'approved' or 'rejected' to indicate the approval status for this approval.  When called, the following information is available:
//
//    counts.total = total number of users that are part of this approval
//    counts.approved = # of users that approved so far
//    counts.rejected = # of users that rejected so far
//    counts.requested = # of users that are pending approval
//    counts.not_requested = # of users that are not pending approval
//    counts.not_required = # of users that approval is not required
//

answer=[];
for(i=0; i<workflow.scratchpad.count; i++)
{
	var approver = workflow.scratchpad.approverList[i];
	if(workflow.scratchpad.routingOrder == approver.routingOrder)
	{
		workflow.scratchpad.userApprovalSend = workflow.scratchpad.userApprovalSend + 1;
		answer.push(workflow.scratchpad.approverList[i].id);
		u_pid_status=workflow.scratchpad.approverList[i].title;
	}
}
gs.info("Approval send to: " + JSON.stringify(answer));
workflow.scratchpad.routingOrder = workflow.scratchpad.routingOrder + 1;

