var identifier = context.name + '.' + activity.name;  
var approverRecords = new GlideRecord('x_178530_pid_pid_approvers'); 
approverRecords.addQuery('u_project_id',  current.sys_id); 
approverRecords.query();  
var approver = {};
var approverList=[];
while(approverRecords.next())
{	
	approver = {};
	gs.info('---> Getting  Approver to list' + approverRecords.u_approver);
	approver.id = approverRecords.u_approver + '';		
	approver.routingOrder = approverRecords.u_routing_order + '';	
	approver.title=approverRecords.u_title +'';
	gs.info("Title of approver"+approverRecords.u_title);
	approverList.push(approver);	
	gs.info("approver = " + JSON.stringify(approver));
	gs.info("approver List = " + JSON.stringify(approverList));
}
workflow.scratchpad.count = approverList.length; // the overall count available  
workflow.scratchpad.approverList = approverList; // the list of incident objects  
workflow.scratchpad.routingOrder = 1;
workflow.scratchpad.userApprovalSend = 0;
gs.info("Records from list = " + JSON.stringify(approverList));
gs.info("Records from list in scratchpad = " + JSON.stringify(workflow.scratchpad.approverList ));

