var checkPreviousApproval = function(approvalSysID){
	for(i=0;i<previousApprovals.length;i++){
		if(previousApprovals[i].approvalID == approvalSysID){
			return true;
		}
	}
	return false;
};

var previousApprovals = workflow.scratchpad.completedApproval;
var currentApprovals = [];
var approvalHistory = {};

var gr= new GlideRecord('sysapproval_approver');
gr.addQuery('document_id', current.sys_id);
gr.query();
while(gr.next()){
	var isPreviousApproval = checkPreviousApproval(gr.sys_id + '');
	gs.info("is previous approval record = " + isPreviousApproval);
	if(!isPreviousApproval){
		approvalHistory = {};
		approvalHistory.approvalID = gr.sys_id + '';
		approvalHistory.orignalApprover = gr.approver + '';
		approvalHistory.orignalApproverName = gr.approver.getDisplayValue() + '';
		approvalHistory.actionPerformed = gr.state + '';
		approvalHistory.approvalLevel = workflow.scratchpad.routingOrder;
		
		gs.info("Getting journals");

		var elementID = gr.sys_id + '';

		var journals = new GlideRecord('sys_journal_field');
		journals.addQuery('element_id', elementID);
		journals.query();
		gs.info("Journals = " + JSON.stringify(journals));
		while(journals.next()){

			approvalHistory.comment = journals.value + '';
			approvalHistory.actualApprover = journals.getValue('sys_created_by');
		}		
		gs.info("Approval Histories =" + JSON.stringify(approvalHistory));
		gs.info("systemID =" + JSON.stringify(gr.getValue('sys_id')));
		currentApprovals.push(approvalHistory);
	}
}

gs.info("Approval Completed =" + JSON.stringify(currentApprovals));


var history = "Approval Level: " + workflow.scratchpad.routingOrder + '\n \n';
for(i=0;i<currentApprovals.length;i++){
	var isSameUser = false;
	var orignalApprover = new GlideRecord('sys_user');
	orignalApprover.addQuery('sys_id', currentApprovals[i].orignalApprover);
	orignalApprover.query();
	
	if(currentApprovals[i].comment){		
		while(orignalApprover.next()){
			var approverUsername = orignalApprover.getValue('user_name');
			if(approverUsername == currentApprovals[i].actualApprover){
				history += "Approver: " + orignalApprover.name + "\n Action performed: " + currentApprovals[i].actionPerformed + "\n Comments: " + currentApprovals[i].comment +"\n";
			} else{
				history += "Orignal approver: " + orignalApprover.name + "\n Action performed: " + currentApprovals[i].actionPerformed + "\n Actual Approver: " + currentApprovals[i].actualApprover + "\n Comments: " + currentApprovals[i].comment +"\n";
			}
		}		
	} else{
		history += "Approver: " + currentApprovals[i].orignalApproverName  + "\n Action performed: " + currentApprovals[i].actionPerformed + "\n";
	}
}
history += "\n \n";
history = workflow.scratchpad.history + history;
gs.info(history);

workflow.scratchpad.completedApproval = previousApprovals.concat(currentApprovals);
workflow.scratchpad.history = history;
gs.info("Updated Completed approvals = " + workflow.scratchpad.completedApproval);

