	approvalHistory.orignalApprover = gr.approver + '';
	approvalHistory.orignalApproverName = gr.approver.getDisplayValue() + '';
	approvalHistory.actionPerformed = gr.state + '';
	approvalHistory.approvalLevel = workflow.scratchpad.routingOrder;
	
	var elementID = gr.sys_id + '';
	
	var journals = new GlideRecord('sys_journal_field');
	journals.addQuery('element_id', elementID);
	journals.query();
	gs.info("Journals = " + JSON.stringify(journals));
	while(journals.next()){
		
		approvalHistory.comment = journals.value + '';
		approvalHistory.actualApprover = journals.getValue('sys_created_by');
	}		
	gs.info("Approval Histories =" + JSON.stringify(approvalHistory));
	gs.info("systemID =" + JSON.stringify(gr.getValue('sys_id')));
	completedApproval.push(approvalHistory);
}  

gs.info("Approval Completed =" + JSON.stringify(completedApproval));

var history = '1st level Approvers \n \n';
for(i=0;i<completedApproval.length;i++){
	var orignalApprover = new GlideRecord('sys_user');
	orignalApprover.addQuery('sys_id', completedApproval[i].orignalApprover);
	orignalApprover.query();
	
	if(completedApproval[i].comment){	
		while(orignalApprover.next()){
			var approverUsername = orignalApprover.getValue('user_name');
			if(approverUsername == completedApproval[i].actualApprover){
				history += "Approver: " + orignalApprover.name + "\n Action performed: " + completedApproval[i].actionPerformed + "\n Comments: " + completedApproval[i].comment + "\n";
			} else{
				history += "Orignal approver: " + orignalApprover.name + "\n Action performed: " + completedApproval[i].actionPerformed  + "\n Actual Approver: " + completedApproval[i].actualApprover + "\n Comments: " + completedApproval[i].comment + "\n";
			}
		}		
	} else{
		history += "Approver: " +  completedApproval[i].orignalApproverName +"\n Action performed: " + completedApproval[i].actionPerformed + "\n";
	}
}
history += "\n \n";
gs.info(history);

workflow.scratchpad.completedApproval = completedApproval;
workflow.scratchpad.history = history;

