gr.query();
while(gr.next()){
	var isPreviousApproval = checkPreviousApproval(gr.sys_id + '');
	gs.info("is previous approval record = " + isPreviousApproval);
	if(!isPreviousApproval){
		approvalHistory = {};
		approvalHistory.approvalID = gr.sys_id + '';
		approvalHistory.actionPerformed = gr.state + '';
		approvalHistory.orignalApprover = gr.approver + '';
		approvalHistory.orignalApproverName = gr.approver.getDisplayValue() + '';
		approvalHistory.approvalLevel = workflow.scratchpad.routingOrder;
		
		gs.info("Getting journals");

		var elementID = gr.sys_id + '';

		var journals = new GlideRecord('sys_journal_field');
		journals.addQuery('element_id', elementID);
		journals.query();
		gs.info("Journals = " + JSON.stringify(journals));
		while(journals.next()){

			approvalHistory.comment = journals.value + '';
			approvalHistory.actualApprover = journals.getValue('sys_created_by');
		}		
		gs.info("Approval Histories =" + JSON.stringify(approvalHistory));
		gs.info("systemID =" + JSON.stringify(gr.getValue('sys_id')));
		currentApprovals.push(approvalHistory);
	}
}

